import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Form, Button, Alert, Spinner } from 'react-bootstrap';
import { FaUser, FaLock, FaBrain, FaShieldAlt } from 'react-icons/fa';
import { useAuth } from '../hooks/useAuth.tsx';

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      await login(email, password);
      navigate('/');
    } catch (error: any) {
      setError(error.response?.data?.message || 'Erreur lors de la connexion');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-vh-100 d-flex align-items-center" style={{
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      <Container>
        <Row className="justify-content-center">
          <Col md={6} lg={5} xl={4}>
            {/* Logo et titre */}
            <div className="text-center mb-4">
              <div className="d-inline-flex align-items-center justify-content-center bg-white rounded-circle mb-3" 
                   style={{ width: '80px', height: '80px' }}>
                <FaBrain size={40} className="text-primary" />
              </div>
              <h1 className="text-white fw-bold mb-2">Gant Neuro</h1>
              <p className="text-white-50 mb-0">Détection précoce des maladies neurologiques</p>
            </div>

            {/* Formulaire de connexion */}
            <Card className="gant-neuro-form border-0">
              <Card.Body className="p-4">
                <div className="text-center mb-4">
                  <h2 className="fw-bold text-dark mb-2">Connexion</h2>
                  <p className="text-muted">Accédez à votre espace personnel</p>
                </div>

                <Form onSubmit={handleSubmit}>
                  {/* Email */}
                  <Form.Group className="mb-3">
                    <Form.Label className="fw-semibold text-dark">
                      <FaUser className="me-2" />
                      Adresse email
                    </Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="votre@email.com"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                      className="form-control-lg"
                      style={{ borderRadius: '10px' }}
                    />
                  </Form.Group>

                  {/* Mot de passe */}
                  <Form.Group className="mb-4">
                    <Form.Label className="fw-semibold text-dark">
                      <FaLock className="me-2" />
                      Mot de passe
                    </Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Votre mot de passe"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                      className="form-control-lg"
                      style={{ borderRadius: '10px' }}
                    />
                  </Form.Group>

                  {/* Message d'erreur */}
                  {error && (
                    <Alert variant="danger" className="border-0" style={{ borderRadius: '10px' }}>
                      <FaShieldAlt className="me-2" />
                      {error}
                    </Alert>
                  )}

                  {/* Bouton de connexion */}
                  <Button
                    type="submit"
                    disabled={isLoading}
                    className="gant-neuro-btn-primary w-100 py-3 mb-3"
                    size="lg"
                  >
                    {isLoading ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                          className="me-2"
                        />
                        Connexion en cours...
                      </>
                    ) : (
                      <>
                        <FaUser className="me-2" />
                        Se connecter
                      </>
                    )}
                  </Button>

                  {/* Lien d'inscription */}
                  <div className="text-center">
                    <p className="text-muted mb-0">
                      Pas encore de compte ?{' '}
                      <Link to="/register" className="text-decoration-none fw-semibold" 
                            style={{ color: 'var(--gant-neuro-secondary)' }}>
                        S'inscrire maintenant
                      </Link>
                    </p>
                  </div>
                </Form>
              </Card.Body>
            </Card>

            {/* Informations de sécurité */}
            <div className="text-center mt-4">
              <div className="d-inline-flex align-items-center bg-white bg-opacity-10 rounded-pill px-3 py-2">
                <FaShieldAlt className="text-white me-2" />
                <small className="text-white-50">Connexion sécurisée par JWT</small>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default LoginPage;
